	public function pkrset(array $bulk): bool
	{
		$ret = true;
		foreach ($bulk as $key => $value) {
			if (is_array($value)) {
				$value = json_decode(json_encode($value));
			}
			$ret = $ret && $this->storage->set($key, $value);
		}
		return true;
	}
}
